/*jslint node: true */
'use strict';

var persistence = require('recyclecalendarpersistence');


exports.helloAPI = function (callback) {
    callback({
        "Hello": "API"
    });
};

exports.error = function (callback) {
    callback(undefined, {
        errorType: "just an example error"
    });
};

exports.getAllCities = function (callback) {
    persistence.getAllCities(callback);
};

exports.getCitiesByName = function (queryString, callback) {
    persistence.getCitiesByName(queryString, callback);
};